});
cy.fixture('articles').then(function (data) {
  var link = data.articles[1].slug;
  cy.intercept('POST', Cypress.env('apiUrl') + "articles/".concat(link, "/favorite"), data);
});
cy.get('app-article-list button').eq(0).click().should('contain', '6');
cy.get('app-article-list button').eq(1).click().should('contain', '11');

var requestBody = {
  "article": {
    "tagList": [],
    "title": "Testing the api",
    "description": "Testing the api",
    "body": "Testing the api"
  }
};
cy.get('@token').then(function (token) {
  cy.request({
    url: Cypress.env('apiUrl') + 'articles/',
    headers: {
      'Authorization': 'Token ' + token
    },
    method: 'POST',
    body: requestBody
  }).then(function (res) {
    expect(res.status).to.equal(200);
  }); //delete the global feed latest post which is created
  cy.contains(/Global Feed/i).click();
  cy.get('.article-preview').first().click();
  cy.get('.article-actions').contains('Delete Article').click(); //verify whether the title is actually delete from the API or Not
  cy.request({
    url: Cypress.env('apiUrl') + 'articles?limit=10&offset=0',
    headers: {
      'Authorization': 'Token ' + token
    },
    method: 'GET'
  }).its('body').then(function (body) {
    expect(body.articles[0].title).not.to.equal('Testing the api');
  });
  cy.contains(/Global Feed/i).click();
});

